mapper.select:
SELECT COUNT(*) as cnt FROM board
WHERE crudcateno = #{crudcateno}
WHERE crudcateno = #{crudcateno} AND (UPPER(btitle) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(bcontent) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(btag) LIKE '%' || UPPER(#{word}) || '%')

SELECT boardno, accountno, managerno, crudcateno, btitle, bcontent, bdate, btag, LOWER(bphoto) as bphoto, bphotosaved, bthumb, bsize, breplycnt, bview, brecom, bvisible, tagno, byoutube, r FROM ( SELECT boardno, accountno, managerno, crudcateno, btitle, bcontent, bdate, btag, LOWER(bphoto) as bphoto, bphotosaved, bthumb, bsize, breplycnt, bview, brecom, bvisible, tagno, byoutube, rownum as r FROM ( SELECT boardno, accountno, managerno, crudcateno, btitle, bcontent, bdate, btag, LOWER(bphoto) as bphoto, bphotosaved, bthumb, bsize, breplycnt, bview, brecom, bvisible, tagno, byoutube FROM board
WHERE crudcateno = #{crudcateno}
WHERE crudcateno = #{crudcateno} AND (UPPER(btitle) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(bcontent) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(btag) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY boardno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM board
WHERE boardno = #{boardno} AND bpasswd=#{bpasswd}

mapper.update:
UPDATE board
SET btitle=#{btitle}, bcontent=#{bcontent}, btag=#{btag}, bvisible=#{bvisible}
WHERE boardno = #{boardno}

UPDATE board
SET bphoto=#{bphoto}, bphotosaved=#{bphotosaved}, bthumb=#{bthumb}, bsize=#{bsize}
WHERE boardno = #{boardno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM board 
WHERE crudcateno=#{crudcateno}

SELECT COUNT(*) as cnt 
FROM board 
WHERE accountno=#{accountno} and managerno=#{managerno}
