mapper.select:
SELECT COUNT(*) AS cnt FROM GPA
WHERE GPANO = #{gpano}
WHERE GPANO = #{gpano} AND (BOARDNO LIKE '%' || #{word} || '%')

SELECT GPANO, GPASCORE, ACCOUNTNO, BOARDNO, GPADATE, r
FROM (
    SELECT GPANO, GPASCORE, ACCOUNTNO, BOARDNO, GPADATE, ROWNUM as r
    FROM (
        SELECT GPANO, GPASCORE, ACCOUNTNO, BOARDNO, GPADATE
        FROM GPA


                WHERE GPANO = #{gpano}


                WHERE GPANO = #{gpano}
                AND (BOARDNO LIKE '%' || #{word} || '%')


        ORDER BY GPANO DESC
    )
)
WHERE  r >= #{start_num} AND r <= #{end_num}

  SELECT ROUND(AVG(GPASCORE), 0) AS avg_gpascore
FROM GPA
WHERE boardno = #{boardno}

mapper.update:
UPDATE GPA
SET GPASCORE = #{gpascore}
WHERE GPANO = #{gpano}
