mapper.update:
UPDATE regionfood
SET foodtitle = #{foodtitle}, foodimg_url = #{foodimg_url}, step_no
WHERE foodno = #{foodno}

mapper.select:
SELECT foodno, foodtitle, foodimg_url,foodimg_urlsaved,fsize, regiono, recipelist, cook_tool, difficulty, cooktime, managerno
FROM regionfood
WHERE foodno = #{foodno}

SELECT foodno, foodtitle, foodimg_url,foodimg_urlsaved,fsize, regiono, recipelist, cook_tool, difficulty, cooktime, managerno, r
FROM (
    SELECT foodno, foodtitle, foodimg_url,foodimg_urlsaved,fsize, regiono, recipelist, cook_tool, difficulty, cooktime, managerno, rownum as r
    FROM (
        SELECT v.foodno, v.foodtitle, v.foodimg_url,v.foodimg_urlsaved,v.fsize,v.regiono,v.recipelist,v.cook_tool,v.difficulty,v.cooktime,v.managerno, m.MNAME
        FROM regionfood v
        INNER JOIN MANAGER m ON v.managerno = m.MANAGERNO


    WHERE v.regiono = #{regiono} 


         WHERE v.regiono = #{regiono}  AND UPPER(v.foodtitle) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(m.MNAME) LIKE '%' || UPPER(#{word}) || '%'  OR UPPER(v.recipelist) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(v.cook_tool) LIKE '%' || UPPER(#{word}) || '%'


        ORDER BY v.foodno ASC
    )
)
WHERE r >= #{start_num} AND r <= #{end_num}
