mapper.select:
SELECT mean
FROM vocabulary
where voca = #{voca}

SELECT COUNT(*) as cnt FROM vocabulary
WHERE vocano = #{vocano}
WHERE vocano = #{vocano} AND (UPPER(voca) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(mean) LIKE '%' || UPPER(#{word}) || '%'

SELECT vocano, voca, mean, managerno, r
FROM (
          SELECT vocano, voca, mean, managerno, rownum as r
          FROM (
                SELECT SELECT vocano, voca, mean, managerno
                FROM vocabulary


                    WHERE vocano = #{vocano}


                    WHERE vocano = #{vocano}  AND (UPPER(voca) LIKE '%' || UPPER(#{word}) || '%' 
                                              OR UPPER(mean) LIKE '%' || UPPER(#{word}) || '%' 


                ORDER BY vocano DESC
                )
)
WHERE  r >= #{start_num} AND r <= #{end_num}

mapper.update:
UPDATE vocabulary
SET voca = #{voca}, mean = #{mean}
WHERE vocano = #{vocano}
